// Function to get random questions from the quizzes
function getRandomQuestions(questions, numQuestions) {
    const shuffledQuestions = questions.sort(() => 0.5 - Math.random());
    return shuffledQuestions.slice(0, numQuestions);
}

// Function to get all questions from multiple quizzes and then select random ones
function getRandomQuestionsFromQuizzes(quizzes, numQuestions) {
    const allQuestions = Object.values(quizzes).flatMap(quiz => quiz.questions);
    return getRandomQuestions(allQuestions, numQuestions);
}

const quizzes = { quiz1, quiz2, quiz3, quiz4, quiz5, quiz6, quiz7, quiz8, quiz9, quiz10, quiz11, quiz12 };

let currentQuestionIndex = 0;
let score = 0;
let quiz;
let incorrectAnswers = [];
let userAnswers = [];

document.addEventListener('DOMContentLoaded', function () {
    const urlParams = new URLSearchParams(window.location.search);
    const quizNumber = urlParams.get('quiz');
    const numQuestions = 10; // Number of random questions to select

    if (quizNumber === 'random') {
        quiz = { questions: getRandomQuestionsFromQuizzes(quizzes, numQuestions) };
    } else if (quizNumber && quizzes[`quiz${quizNumber}`]) {
        quiz = { questions: getRandomQuestions(quizzes[`quiz${quizNumber}`].questions, numQuestions) };
    } else {
        console.error('No quiz specified or quiz not found');
        return;
    }
    displayQuiz();
});

function displayQuiz() {
    const quizContainer = document.getElementById('quiz-container');
    const quizStatus = document.getElementById('quiz-status');
    const questions = quiz.questions;

    if (questions.length === 0) {
        console.error('No questions found in the quiz');
        return;
    }

    quizContainer.innerHTML = ''; // Clear previous content if any

    quizStatus.innerHTML = `Question ${currentQuestionIndex + 1} of ${questions.length}`;

    const question = questions[currentQuestionIndex];
    const questionText = question.text;
    const type = question.type;
    const answers = question.answers;
    const explanation = question.explanation;

    const questionDiv = document.createElement('div');
    questionDiv.classList.add('question');
    questionDiv.style.display = 'block';

    const questionLabel = document.createElement('p');
    questionLabel.textContent = questionText;
    questionDiv.appendChild(questionLabel);

    for (let j = 0; j < answers.length; j++) {
        const answer = answers[j];
        const answerText = answer.text;
        const correct = answer.correct;

        const answerDiv = document.createElement('div');
        const input = document.createElement('input');
        input.type = type === 'single' ? 'radio' : 'checkbox';
        input.name = `question${currentQuestionIndex}`;
        input.value = correct;
        input.dataset.correct = correct;
        answerDiv.appendChild(input);

        if (userAnswers[currentQuestionIndex] && userAnswers[currentQuestionIndex].includes(answerText)) {
            input.checked = true;
        }

        const label = document.createElement('label');
        label.textContent = answerText;
        answerDiv.appendChild(label);

        questionDiv.appendChild(answerDiv);
    }

    const explanationDiv = document.createElement('div');
    explanationDiv.classList.add('explanation');
    explanationDiv.textContent = explanation;
    explanationDiv.style.display = 'none';
    questionDiv.appendChild(explanationDiv);

    quizContainer.appendChild(questionDiv);

    document.getElementById('prev-button').style.display = currentQuestionIndex > 0 ? 'block' : 'none';
    document.getElementById('next-button').style.display = currentQuestionIndex < questions.length - 1 ? 'block' : 'none';
    document.getElementById('submit-button').style.display = currentQuestionIndex === questions.length - 1 ? 'block' : 'none';
}

function nextQuestion() {
    saveAnswer();
    currentQuestionIndex++;
    displayQuiz();
}

function prevQuestion() {
    saveAnswer();
    currentQuestionIndex--;
    displayQuiz();
}

function saveAnswer() {
    const questionDiv = document.querySelector('.question');
    const inputs = questionDiv.getElementsByTagName('input');
    const explanationDiv = questionDiv.getElementsByClassName('explanation')[0];
    let correct = true;
    let selectedAnswers = [];

    for (let j = 0; j < inputs.length; j++) {
        const input = inputs[j];
        if (input.checked) {
            selectedAnswers.push(input.nextElementSibling.textContent);
        }
        if (input.type === 'radio' && input.checked && input.dataset.correct === 'false') {
            correct = false;
        } else if (input.type === 'checkbox' && ((input.checked && input.dataset.correct === 'false') || (!input.checked && input.dataset.correct === 'true'))) {
            correct = false;
        }
    }

    userAnswers[currentQuestionIndex] = selectedAnswers;

    if (!correct) {
        incorrectAnswers[currentQuestionIndex] = {
            question: questionDiv.querySelector('p').textContent,
            correctAnswer: Array.from(questionDiv.querySelectorAll('input[data-correct="true"]')).map(input => input.nextElementSibling.textContent).join(', '),
            userAnswer: selectedAnswers.join(', '),
            explanation: explanationDiv.textContent
        };
    } else {
        incorrectAnswers[currentQuestionIndex] = null;
        score++;
    }
}

function submitQuiz() {
    saveAnswer();

    // Hide the quiz container
    const quizContainer = document.getElementById('quiz-container');
    quizContainer.style.display = 'none';

    const resultContainer = document.getElementById('result');
    const explanationsContainer = document.getElementById('explanations');
    
    resultContainer.innerHTML = `<p class="score">Your score: <span class="bold">${score}/${quiz.questions.length}</span></p>`;
    explanationsContainer.innerHTML = '';

    incorrectAnswers.forEach((item, index) => {
        if (item) {
            const explanationDiv = document.createElement('div');
            explanationDiv.classList.add('explanation');
            explanationDiv.innerHTML = `
                <p class="question"><span class="bold">Question ${index + 1}:</span> ${item.question}</p>
                <p><span class="bold">Your answer:</span> ${item.userAnswer}</p>
                <p><span class="bold">Correct answer:</span> ${item.correctAnswer}</p>
                <p><span class="bold">Explanation:</span> ${item.explanation}</p>
            `;
            explanationsContainer.appendChild(explanationDiv);
        }
    });

    // Add a button to review incorrect questions
    if (incorrectAnswers.some(answer => answer !== null)) {
        const reviewIncorrectButton = document.createElement('button');
        reviewIncorrectButton.textContent = 'Review Incorrect Questions';
        reviewIncorrectButton.onclick = reviewIncorrectQuestions;
        explanationsContainer.appendChild(reviewIncorrectButton);
    }

    // Hide the previous button
    document.getElementById('prev-button').style.display = 'none';
    document.getElementById('back-button').style.display = 'block';
    document.getElementById('submit-button').style.display = 'none';

    // Add a button to review the questions
    const reviewButton = document.createElement('button');
    reviewButton.textContent = 'Review Questions';
    reviewButton.onclick = reviewQuestions;
    explanationsContainer.appendChild(reviewButton);
}

function reviewIncorrectQuestions() {
    incorrectAnswers = incorrectAnswers.filter(answer => answer !== null);
    quiz.questions = incorrectAnswers.map(item => {
        return {
            text: item.question,
            type: 'single', // assuming single type for simplification, adjust as needed
            answers: quiz.questions.find(q => q.text === item.question).answers,
            explanation: item.explanation
        };
    });
    currentQuestionIndex = 0;
    score = 0; // Reset the score
    displayQuiz();
    document.getElementById('quiz-container').style.display = 'block';
    document.getElementById('result').innerHTML = '';
    document.getElementById('explanations').innerHTML = '';
    document.getElementById('back-button').style.display = 'none';
}

function reviewQuestions() {
    currentQuestionIndex = 0;
    score = 0; // Reset the score
    displayQuiz();
    document.getElementById('quiz-container').style.display = 'block';
    document.getElementById('result').innerHTML = '';
    document.getElementById('explanations').innerHTML = '';
    document.getElementById('back-button').style.display = 'none';
}

function goBack() {
    window.location.href = 'index.html';
}
function displayResultImage(score) {
    let imageSrc;
    if (score >= 1 && score <= 4) {
        imageSrc = '1.png';
    } else if (score >= 5 && score <= 7) {
        imageSrc = '2.png';
    } else {
        imageSrc = '3.png';
    }

    const resultContainer = document.getElementById('result');
    const image = document.createElement('img');
    image.src = imageSrc;
    image.alt = 'Quiz Result Image';
    resultContainer.appendChild(image);
}

